import React, { useState, useEffect, useCallback, useRef } from "react";
import { Container } from "@material-ui/core";
import { styled } from "@material-ui/core/styles";
import Message from "./message";
import moment from "moment";
import { Virtuoso } from 'react-virtuoso'

const MessagesContainer = styled(Container)({
  background: "inherit",
  display: "flex",
  flexDirection: "column",
  overflowY: "scroll",
  height: "inherit",
  maxHeight: "inherit",
});



const MessageContainer = (props) => {
  const { messages, username } = props;

  const GenerateMessage = (index) => (
    <Message
    key = {`key-msg-${index}`}
    timeMessage={moment(messages[index].timeMessage).calendar()}
    authorMessage={messages[index].authorMessage}
    textMessage={messages[index].textMessage}
    username={username}
    colorNickName={messages[index].colorAuthorName}
   />
  );
  const virtuoso = useRef(null)
  return (
    <MessagesContainer>
      {/* {messages.map((item, index) => (
        <Message
          key = {`key-msg-${index}`}
          timeMessage={moment(item.timeMessage).calendar()}
          authorMessage={item.authorMessage}
          textMessage={item.textMessage}
          username={username}
          colorNickName={item.colorAuthorName}
        />
      ))} */}
        <Virtuoso
          totalCount={messages.length}
          ref={virtuoso}
          item={GenerateMessage}
        />
    </MessagesContainer>
  );
};

export default MessageContainer;
