import React, { useState, useEffect, useCallback, useRef } from "react";
import { Container } from "@material-ui/core";
import { styled } from "@material-ui/core/styles";
import Message from "./message";
import moment from "moment";

const MessagesContainer = styled(Container)({
  background: "inherit",
  display: "flex",
  flexDirection: "column",
  overflowY: "scroll",
  height: "inherit",
  maxHeight: "inherit",
});

const MessageContainer = (props) => {
  const { messages, username } = props; 
  const myRef = useRef(null);

  useEffect(() => {
    console.log('ref', myRef);
    console.log('ref', myRef.current.scrollHeight   );
    myRef.current.scrollTop = myRef.current.scrollHeight;
}, [messages, myRef]);

//   const GenMessage = (item, index) => (
//     <Message
//     key = {`key-msg-${index}`}
//     timeMessage={moment(item.timeMessage).calendar()}
//     authorMessage={item.authorMessage}
//     textMessage={item.textMessage}
//     username={username}
//     colorNickName={item.colorAuthorName}
//    />
//   )
  return (
    <MessagesContainer ref = {myRef}>
      {messages.map((item, index) => (
        <Message
          key = {`key-msg-${index}`}
          timeMessage={moment(item.timeMessage).calendar()}
          authorMessage={item.authorMessage}
          textMessage={item.textMessage}
          username={username}
          colorNickName={item.colorAuthorName}
        />
      ))}
    </MessagesContainer>
  );
};

export default MessageContainer;
